package oc;public class OptionsGruppeEintrag extends Info implements Comparable<OptionsGruppeEintrag> {	String name;	String kurzerName = null;	double preis;	double syncPreis;	public String getName() {		return this.name;	}	public String getKurzerName() {		return this.kurzerName;	}	public double getPreis() {		return this.preis;	}	public double getSyncPreis() {		return this.syncPreis;	}	public OptionsGruppeEintrag(String name) {		this.name = name;		this.preis = -88;		this.syncPreis = 0;	}	public OptionsGruppeEintrag(String name, double preis) {		this.name = name;		this.preis = preis;		this.syncPreis = 0;	}	public OptionsGruppeEintrag(String kurzerName, String name) {        this.kurzerName = kurzerName;		this.name = name;		this.preis = -88;		this.syncPreis = 0;	}	public OptionsGruppeEintrag(String name, double preis, int syncPreis) {		this.name = name;		this.preis = preis;		this.syncPreis = syncPreis;	}	public OptionsGruppeEintrag(String kurzerName, String name, double preis) {		this.kurzerName = kurzerName;		this.name = name;		this.preis = preis;		this.syncPreis = 0;	}	public OptionsGruppeEintrag(String kurzerName, String name, double preis, int syncPreis) {		this.kurzerName = kurzerName;		this.name = name;		this.preis = preis;		this.syncPreis = syncPreis;	}		public int compareTo(OptionsGruppeEintrag a) {		return (int)(a.getPreis() - this.getPreis());	}}